% Gyro modulation simulationclose all% Gyro constantsIo = 2e-3; %WL = 200; %mD = 30e-2; %mlambda = 1550e-9; %mRot = 1e-1; %degc = 3e8;% time vectort = -2*pi:0.001:2*pi;% phasedphi_s = 2*pi*L*D/(lambda*c) * Rot;% Sagnac phase (rotation)%dphi_r = %ramp modulation %dphi_m = % check ref Lefreve 1985 paperphi  = dphi_s;% + dphi_r + dphi_m;% modulation signaltau = 1;sq = *square(2*pi*2*t,50);I = Io*(1 + cos(phi*t));%plots figureplot(I)figureplot(t,sq)ylim([-1.1 1.1])##figure##plot(t,Io*(1 + cos(phi*t+ sq)))